mapper.select:
select aid, aname, pwd, rid, photo, remark, status from adminInfo where 1=1

	and aname like concat('%',#{aname},'%')



	and rid=#{rid}



	and status=#{status}

order by aid limit #{page}, #{rows}

select count(aid) from adminInfo where 1=1

	and aname like concat('%',#{aname},'%')



	and rid=#{rid}



	and status=#{status}
